settings.custom_css:
@media (min-width: 769px) {
  .rivo-hero-banner-wrapper {
    padding: 2rem;
  }
  .rivo-hero-banner-text {
    display: flex;
    flex-direction: column;
    height: 100%;
    align-self: center;
  }
  #rivo-lp-header {
    display: flex;
  }
  .rivo-how-it-works-step-image img {
    margin: 0 auto;
    object-fit: cover;
    height: 200px;
  }
  #rivo-vip-tiers .rivo-lp-section-subtitle {
    max-width: 60%;
  }
}

.rivo-lp-content {
  max-width: var(--page-width);
}

.rivo-lp-cards-container {
  max-width: 100%;
}

.rivo-hero-banner-wrapper {
  max-width: 1500px;
}

.rivo-hero-description {
  padding-top: 0;
}

.rivo-content-wrapper {
  padding-left: 15px;
  padding-right: 15px;
}

.rivo-lp-section-title {
  line-height: 1.2;
}

.rivo-hero-buttons a:first-of-type {
  background: var(--rivo-primary-color);
  border-color: var(--rivo-primary-color);
  color: #000;
}

.rivo-hero-buttons a:last-of-type {
  background: #fff;
  border-color: #fff;
  color: #000;
}

.rivo-hero-buttons a:hover {
  background: #000 !important;
  border-color: #000;
  color: #fff !important;
}

#rivo-lp-explainer .rivo-lp-content {
  max-width: 1200px;
}

/* --- REWARDS --- */

.rivo-card-title,
.rivo-card-description {
  line-height: 1.1;
}

.rivo-card-description {
  padding-top: 5px;
}

/* --- VIP TIERS --- */

th.rivo-vip-tier-table-thead-tier-cell {
  border-top-left-radius: 50%;
  border-top-right-radius: 50%;
  padding: 4rem 3rem 2rem 3rem !important;
}

table.rivo-vip-tier-table td {
  height: 65px;
}

.rivo-tier-column-3 {
  color: #fff;
}

.rivo-vip-tier-container {
  max-width: 1200px;
}

table.rivo-vip-tier-table {
  margin-bottom: 10px;
}

/* Icon */
span.rivo-th-tier-image {
  display: none;
}

span.rivo-th-tier-name {
  line-height: 1.2;
  min-height: 50px;
  align-content: center;
  max-width: 200px;
  margin-left: auto;
  margin-right: auto;
}

/* First column */
td.rivo-vip-tier-table-row-perk-name {
  text-align: left;
}

/* Table text */
td.rivo-vip-tier-table-row-perk-value {
  text-align: center;
}

span.rivo-th-tier-threshold {
  font-weight: 400;
  background: #fff;
  width: 80%;
  color: #000;
  border-radius: 100px;
  margin: 0 auto;
}

.rivo-tier-column-1 span.rivo-th-tier-threshold,
.rivo-tier-column-3 span.rivo-th-tier-threshold {
  background: var(--rivo-primary-color);
}

/* --- VIP TIERS PROGRESS BAR --- */

.rivo-vip-tier-progress-container {
  width: 50%;
}

@media (max-width: 768px) {
  .rivo-vip-tier-progress-container {
    width: 95%;
  }
}

.rivo-account-progress-container {
  background: var(--rivo-primary-color);
}

.rivo-account-progress-bar {
  background: var(--rivo-secondary-color);
}

.rivo-progress-current-tier-label,
.rivo-progress-next-tier-label {
  text-transform: uppercase;
}

.rivo-vip-tier-progress-container.rivo-vip-tier-columns {
  justify-content: center;
}

/* --- ACTIVITY --- */

.rivo-points-expiry {
  padding: 10px;
  background: #fafafa;
  width: fit-content;
  margin: 0 auto;
  border-radius: 0.75rem;
  font-size: 12px;
  margin-top: 10px;
}

#rivo-lp-my-rewards .rivo-lp-content {
  padding: 25px;
  margin: 0 10px;
  border: 1px solid #000;
}

/* — MODAL — */

.rivo-modal__title,
.rivo-card-title,
.rivo-how-it-works-step-title {
  font-family: var(--rivo-title-font-family);
}

.rivo-modal-button {
  font-size: 16px;
}

.rivo-modal-button:before {
  background-color: #000;
  width: 100%;
  height: 100%;
  border-radius: 4px;
  border: none !important;
  background-position: center;
}

input.rivo-coupon-code,
input.rivo-form-input {
  font-size: 16px !important;
  border-radius: 5px;
  border: 1px solid var(--rivo-secondary-color);
  background: var(--rivo-primary-color);
}

.rivo-copy-button.rivo-success-button::before,
button[aria-label="Copy coupon code"]::before {
  background-color: var(--rivo-primary-color);
  border-radius: 100px;
  padding: 5px;
}

button.rivo-copy-button {
  background-color: transparent;
  cursor: pointer;
  border: none;
  position: absolute !important;
  right: 25px;
}

.rivo-modal-p a {
  text-decoration: underline;
}

.rivo-modal-p a:after {
  content: ".";
}

#points-logs-activity th {
  border-bottom: 3px solid var(—rivo-primary-color);
}

#points-logs-activity td {
  font-size: 14px;
  padding-top: 20px;
  padding-bottom: 20px;
  border: none;
}

.rivo-points-table-load-more-button {
  padding: 0;
  border: none;
  background: transparent;
}

/* — HOW IT WORKS — */

.rivo-how-it-works-step-image img {
  margin: 0 auto;
}

.rivo-how-it-works-step-number {
  background: var(--rivo-secondary-color);
  color: #fff;
  font-size: 24px;
  border-radius: 50%;
  width: 60px;
  height: 60px;
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 0 auto;
}

/* — REFERRALS — */

@media (max-width: 160rem) {
  #rivo-lp-referral-campaign,
  #rivo-lp-referral-campaign .rivo-lp-content {
    padding: 0;
  }
}

iframe.rivo-referral {
  margin: 0 auto;
}

/* --- CARDS --- */

.rivo-arrow-icon {
  opacity: 1;
  background-image: url("https://cdn.shopify.com/s/files/1/0047/6862/8836/files/Rivo_Icon_Arrow_Left.svg?v=1762096285");
  background-repeat: no-repeat;
  background-size: contain;
}

.rivo-completed {
  opacity: 1;
  background-image: url("https://cdn.shopify.com/s/files/1/0047/6862/8836/files/Rivo_Icon_Success.svg?v=1762096285");
  background-repeat: no-repeat;
  background-size: contain;
}

.rivo-arrow-icon svg,
.rivo-completed svg {
  visibility: hidden;
}

#rivo-ways-to-redeem .rivo-card-icon img {
  width: 150px !important;
  height: 150px !important;
}

#rivo-lp-my-rewards .rivo-card-icon img {
  width: 120px !important;
  height: 120px !important;
}

#rivo-ways-to-earn .rivo-card-body {
  max-width: 90%;
}

/* — MOBILE — */

@media (max-width: 749px) {
  .rivo-content-wrapper {
    padding-left: 10px;
    padding-right: 10px;
  }
  .rivo-mobile-banner-overlay .rivo-lp-content {
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
  }
  .rivo-lp-cards-container {
    width: 100% !important;
  }
  .rivo-hero-banner-text {
    padding-top: 40px;
    padding-bottom: 40px;
  }
  #rivo-lp-my-rewards .rivo-lp-content {
    padding: 25px 10px;
    margin: 0 10px;
  }
  .rivo-card-icon svg,
  .rivo-card-icon img {
    height: 40px !important;
    width: 40px !important;
  }
  .rivo-how-it-works-step-image img {
    height: 100px;
  }
  .rivo-card-title {
    font-size: 16px;
  }
  .rivo-card-description {
    font-size: 13px;
  }
  /* --- VIP Tiers Table Scroll --- */

  .rivo-vip-tier-container {
    width: 100%;
    overflow: auto;
    overflow-x: scroll; /* Always show horizontal scrollbar */
  }

  table.rivo-vip-tier-table th,
  table.rivo-vip-tier-table td {
    width: 160px;
    height: 60px;
  }

  .rivo-vip-tier-table-thead-first-cell {
    background: #fff;
  }

  .rivo-vip-tier-table-thead-first-cell,
  td.rivo-vip-tier-table-row-perk-name {
    position: sticky;
    left: 0;
  }

  /* --- SCROLL BAR --- */

  .rivo-lp-cards-container::-webkit-scrollbar,
  .rivo-vip-tier-container::-webkit-scrollbar {
    width: 10px;
    height: 8px;
  }

  .rivo-vip-tier-container::-webkit-scrollbar-track {
    background-color: var(--rivo-primary-color);
    border-radius: 10px;
  }

  .rivo-vip-tier-container::-webkit-scrollbar-thumb {
    background: var(--rivo-secondary-color);
    border-radius: 10px;
  }

  .rivo-vip-tier-container::-webkit-scrollbar-thumb:hover {
    background: var(--rivo-secondary-color);
  }

  /* Firefox only */
  @-moz-document url-prefix() {
    .rivo-vip-tier-container {
      overflow-x: scroll;
      scrollbar-width: auto; /* or thin */
      scrollbar-color: var(--rivo-secondary-color) var(--rivo-primary-color);
    }
  }
  th.rivo-vip-tier-table-thead-tier-cell {
    padding: 3rem 2rem 1rem 2rem !important;
  }
  span.rivo-th-tier-threshold {
    width: 95%;
  }
  #rivo-lp-my-rewards .rivo-card {
    min-width: 90%;
    width: 90%;
    padding: 15px 10px;
    flex-direction: row;
    text-align: left;
    position: relative;
    border-radius: 10px;
  }
  #rivo-lp-my-rewards .rivo-card-title,
  #rivo-lp-my-rewards .rivo-card-description {
    padding: 0;
    text-align: left;
  }
  #rivo-lp-my-rewards .rivo-card-body {
    align-items: baseline;
    text-align: left;
  }
  #rivo-ways-to-redeem .rivo-card {
    width: 90%;
    min-width: 90%;
  }
  #rivo-ways-to-redeem .rivo-card-icon img {
    width: 120px !important;
    height: 120px !important;
  }
  #rivo-lp-my-rewards .rivo-card-icon img {
    width: 70px !important;
    height: 70px !important;
  }
  span.rivo-th-tier-name {
    font-size: 24px;
  }
   .rivo-how-it-works-step-title {
    font-size: 20px;
     margin-bottom: 5px;
  }
   .rivo-how-it-works-step-subtitle {
    font-size: 14px;
  }
}
